    var maxTemperature, minTemperature, averageTemperature;
    {posts.map(post => (
             <li key = {post.date}>
               this.date={post.date}
               this.maxTemperature={post.max}
               this.minTemperature={post.minTemperature}
               this.averageTemperature={post.averageTemperature}
             </li>
           ))}
     return { date, maxTemperature, minTemperature, averageTemperature };
  }

  export default function Chart() {
    const [posts, setPosts] = useState([]);

    useEffect(async () => {
      /*axios({
        method: 'GET',
        url:'api/formerData/temperature'
      }).then(response => setPosts(response.data))*/

      try{
        const response = await axios.get('http://220.149.31.104:8080/api/formerData/temperature/list')
        setPosts(response.data);
      }catch(error){
        console.log(error)
      }

    })

    const theme = useTheme();
  
    return (
      <div>
      <ul>
          {posts.map(post => (
            <li key = {post.date}>
