            </li>
          ))}
      </ul>

      <React.Fragment>
        <Title>Today</Title>
        <ResponsiveContainer>
          <AreaChart
            data={createData}
            margin={{
              top: 16,
              right: 16,
              bottom: 0,
              left: 24,
            }}
          >
            <XAxis
              dataKey="time"
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            />
            <YAxis
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            >
              <Label
                angle={270}
                position="left"
                style={{
                  textAnchor: 'middle',
                  fill: theme.palette.text.primary,
                  ...theme.typography.body1,
                }}
              >
                Temperature

              </Label>
            </YAxis>
            <Area type="monotone" dataKey="maxTemperature" stroke="#ffc658" fill="#ffc658" />
