import { useTheme } from '@mui/material/styles';
import { AreaChart, Line, XAxis, YAxis, Label, ResponsiveContainer } from 'recharts';
import Title from './Title';

import axios from 'axios';
import React, { useEffect, useState} from 'react';
import qs from 'qs';

var port=1;

const DataRequest = () => {
    const [datas, setDatas] = useState([]);
    

    useEffect(() => { 
        axios({
        method: "get", // 통신 방식
        url: "http://220.149.31.104:9090/api/formerData/temperature/list"+"/"+port, // 서버
        params: {
            startDate : "2022-01-01",
            endDate : "2022-03-06"
        },
        paramsSerializer: function (params) {
            return qs.stringify(params, {arrayFormat: 'brackets'})
        },
        responseType: 'json',
        headers: {'Content-Type': `application/json`},
        })

// axios 정의 마침.
// axios.then 시작.
        .then(res => {
            console.log(res.data); // [{}, ... ,{}] 형태
            return res.data;
        })
        .then(data => {
            setDatas(JSON.stringify(data));
            
            // console.log(response.success)
            // console.log(response.status)
            // console.log(response.statusText)
            // console.log(response.headers)
            // console.log(response.config)
        })
    },[]);

    return(
        <>
        {/* 출력테스트1 */}
        {datas.date},
        {datas.max},
        {datas.date},
        {datas.date}

        
        </>
    );

}

function createData(date, max, min, average) {
    return { date, max, min, average };
  }
  
  const tem = [
    createData("2022-01-01", 7.54, 4.43, 6.22)
  ];
  
  export default function Chart() {
    const theme = useTheme();
    
        <div>
          {tem.map(data =>{
            return (<div key={data.id}>
              {data.date},
              {data.max},
              {data.min},
              {data.average}
            </div>)
          })}
        </div>
      
    
  
    return (
      <React.Fragment>
        <Title>Today</Title>
        <ResponsiveContainer>
          <AreaChart
            data={tem}
            margin={{
              top: 16,
              right: 16,
              bottom: 0,
              left: 24,
            }}
          >
            <XAxis
              dataKey="time"
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            />
            <YAxis
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            >
              <Label
                angle={270}
                position="left"
                style={{
                  textAnchor: 'middle',
                  fill: theme.palette.text.primary,
                  ...theme.typography.body1,
                }}
              >
                Temperature

              </Label>
            </YAxis>
            <Line
              isAnimationActive={false}
              type="monotone"
              dataKey="amount"
              stroke={theme.palette.primary.main}
              dot={false}
            />
          </AreaChart>
        </ResponsiveContainer>
      </React.Fragment>
    );
  }
