import React, { useState, useEffect } from 'react';
import { useTheme } from '@mui/material/styles';
import { LineChart, AreaChart, Area, Tooltip, Line, XAxis, YAxis, Label, ResponsiveContainer } from 'recharts';
import Title from './Title';
import axios from 'axios';

function createData(time, max, min) {
    return { time, max, min };
  }
  
  const rangeData = [
    {
      "time": "00:00",
      "max": [
        0, 26.5
      ],
      "min": [
        0, 23.0
      ]

    },
    {
      "time": "03:00",
      "max": [
        0, 25.7
      ],
      "min": [
        0, 24.0
      ]
    },
    {
      "time": "05:00",
      "max": [
        0, 23.0
      ],
      "min": [
        0, 21.0
      ]
    },
    {
      "time": "07:00",
      "max": [
        0, 26.0
      ],
      "min": [
        0, 23.0
      ]
    },
    {
      "time": "09:00",
      "max": [
        0, 25.0
      ],
      "min": [
        0, 21.0
      ]
    }

  ]
  const data = [
    createData('00:00', 26.5, 23.0),
    createData('03:00', 25.7, 24.0),
    createData('06:00', 23.0, 22.0),
    createData('09:00', 26.5, 24.7),
    createData('12:00', 24.0, 21.3),
    createData('15:00', 25.0, 22.4),
    createData('18:00', 25.6, 23.8),
    createData('21:00', 26.5, 24.2),
    createData('24:00', undefined),
  ];
  
  export default function Chart() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
      axios({
        method: 'GET',
        url:'api/formerData/temperature'
      }).then(response => setPosts(response.data))
    })

    const theme = useTheme();
  
    return (
      <div>
      <ul>
          {posts.map(post => (
            <li key = {post.date}>
              <div>{post.maxTemperature}</div>
              <div>{post.minTemperature}</div>
              <div>{post.average}</div>


            </li>
          ))}
      </ul>

      <React.Fragment>
        <Title>Today</Title>
        <ResponsiveContainer>
          <AreaChart
            data={rangeData}
            margin={{
              top: 16,
              right: 16,
              bottom: 0,
              left: 24,
            }}
          >
            <XAxis
              dataKey="time"
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            />
            <YAxis
              stroke={theme.palette.text.secondary}
              style={theme.typography.body2}
            >
              <Label
                angle={270}
                position="left"
                style={{
                  textAnchor: 'middle',
                  fill: theme.palette.text.primary,
                  ...theme.typography.body1,
                }}
              >
                Temperature

              </Label>
            </YAxis>
            <Area type="monotone" dataKey="max" stroke="#ffc658" fill="#ffc658" />
            <Area type="monotone" dataKey="min" stroke="#82ca9d" fill="#82ca9d" />
            <Tooltip />
          </AreaChart>
        </ResponsiveContainer>
      </React.Fragment>
      </div>
    );
  }
