import axios from 'axios';
import React, { useEffect, useState} from 'react';

import PropTypes from 'prop-types';

function Movie({id, title, year, summary, poster}){
    return (
        <div class="temperature__data">
            <h3 class="temperature_date">{date}</h3>
            <h5 class="temperature_max">{max}</h5>
            <h5 class="temperature_min">{min}</h5>
            <h5 class="temperature_average">{average}</h5>
        </div>
    );
}
Movie.propTypes ={
    id:PropTypes.number.isRequired,
    year:PropTypes.number.isRequired,
    title:PropTypes.string.isRequired,
    summary:PropTypes.string.isRequired,
    poster:PropTypes.string.isRequired,
};

class Test extends React.Component{
    state ={
        isLoading: true,
        dates:[],
    };

    getDates = async() => {
        const{
            data:{
                data: {dates},
            },
        } = await axios.get("https://yts-proxy.now.sh/list_movies.json");
        console.log(dates);
        this.setState({dates, isLoading:false});
    };

    componentDidMount(){
        //여기서 영화 데이터 로딩!
        // setTimeout(() => {
        //     this.setState({isLoading:false});
        // },6000);
        this.getDates();
    }

    render(){
        const {isLoading, dates} = this.state;
        return (
            <section class="container">
                {isLoading?(
                    <div class="loader">
                        <span class="loader__text">Loading...</span>
                    </div>
                    ):(
                        <div class="dates">
                            {dates.map((date)=>(
                                <Movie
                                    key={date.id}
                                    id={movie.id}
                                    max={movie.year}
                                    min={movie.title}
                                    average={movie.summary}
                                    poster={movie.medium_cover_image}
                                />
                            ))}
                        </div>
                )}
            </section>

        );
    }
}

export default Test;
