import React, { useState } from 'react';
import './login.css';
import TextField from '@mui/material/TextField';
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';
import FormControlLabel from '@mui/material/FormControlLabel';
// import Link from '@mui/material/Link';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import 'url-search-params-polyfill';
import { Token } from '@mui/icons-material';
import { cookies, setCookie, useCookies } from "react-cookie";
axios.defaults.withCredentials = true;

const JWT_EXPIRY_TIME = 24 * 3600 * 1000;

onLoginSuccess = response => {
  setCookie('access-token', response.headers["authorization"]);
  setCookie('refresh-token', response.headers["authorization-refresh"]);
  // refreshToken 헤더에 설정
  axios.defaults.headers.common['Authorization'] = `Bearer ${response.headers["authorization-refresh"]}`;

  // accessToken 만료하기 1분 전에 로그인 연장
  setTimeout(onSilentRefresh, JWT_EXPIRRY_TIME - 60000);
}

onSilentRefresh = () => {
  axios.post('//', data)
      .then(onLoginSuccess)
      .catch(function (error) {
        console.log(error);
      })
}

const Login = () => {
  const navigate = useNavigate(); // <-- call hook to get navigate function

  const [message, setMessage] = React.useState('');
  const [Id, setid] = React.useState('');
  const [pw, setPassword] = React.useState('');
  const [cookies, setCookie, removeCookie] = useCookies(["user-name"]);

  const signIn = () => {
    // const data = { userId, password };

    const data = {}
    data.username = Id
    data.password = pw

    axios.post("/api/v1/login", data, { withCredentials: true })
      .then(function (response) {
        if (response.data.success == true) {
          console.log(response);
          console.log("Successfully Logged in ");
          console.log(response.headers["authorization"]);
          (onLoginSuccess);
          setCookie('user-name', Id);
          navigate('/');
        }
        else if (response.data.success == false) {
          console.log("login failed");
          alert(response.data.message)
        }
      })
      .catch(function (error) {
        console.log(error);
      })

  }




  return (
    <div className="page-container">
      <div className="Loginbox">
        <div className="login-leftside">
          <div className="namebox">
            LORA IOT<br />
            DATA MANAGEMENT
          </div>
        </div>
        <div className="login-rightside">
          <Box
            sx={{
              width: '75%',
              marginTop: 8,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Grid container>
              <Grid item xs>
                <FormControlLabel
                  control={<Checkbox value="admin" color="primary" />}
                  label="관리자"
                />
              </Grid>
              <Grid item>
                <FormControlLabel
                  control={<Checkbox value="user" color="primary" />}
                  label="사용자"
                />
              </Grid>
            </Grid>

            <TextField className="IDbox"
              margin="normal"
              label="ID"
              required
              fullWidth
              name="ID"
              id="Id"
              autoFocus
              onChange={e => setid(e.target.value)}
            />
            <TextField
              margin="normal"
              label="pw"
              type="password"
              id="pw"
              required
              fullWidth
              name="pw"
              autoComplete="current-password"
              onChange={e => setPassword(e.target.value)}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              onClick={signIn}
            >
              login
            </Button>

            {/* <Link>
                Forgot password?
              </Link> */}
          </Box>
        </div>
      </div>
    </div>

  );
}

export default Login;
